class PreCheck
{
    public $disabled_functions;
    private $needs;
    private $hidePass;
    private $minVersion;
    private $maxVersion;
    private $curVersion;
    private $fail;
    private $ext;
    private $dirs;
    private $settings;
    private $warn;
    private $items;
    private $extw;
    public function __construct( $hide = 0 )
    {
        $this->hidePass           = $hide;
        $this->fail               = 0;
        $this->warn		  = 0;
        $this->ext                =	$this->extw		= $this->dirs	= $this->disabled_functions	= array();
        self::setDisabled();
        self::getVersion();
        $this->items = array();
    }
    private function checkSetting()
    {
        global $LANG;
        foreach ( $this->settings as $setting ) {
            if ( $this->boolcheck( ini_get( $setting['name'] ) ) != $setting['value'] ) {
                $this->fail( $this->parse( $LANG['badconfig'], array(
                     $setting['name'],
                    ( $setting['value'] == "false" ) ? "Off" : "On" 
                ) ), "Ensure Required Settings are Valid");
            } //$this->boolcheck( ini_get( $setting['name'] ) ) != $setting['value']
            else {
                $this->success( $this->parse( $LANG['goodconfig'], array(
                     $setting['name'] 
                ) ) ,"Ensure Required Settings are Valid");
            }
        } //$this->settings as $setting
    }
    private function boolcheck( $item )
    {
        if ( $item == '' )
            return "false";
        if ( $item === 1 )
            return "true";
        if ( $item === 0 )
            return "false";
        if ( $item == true )
            return "true";
        if ( $item == false )
            return "false";
        return $item;
    }
    public function addSetting( $set, $val )
    {
        $this->settings[] = array(
             "name" => $set,
            "value" => $val 
        );
    }
    public function pass()
    {
        global $LANG;
        if ( $this->hidePass ) {
            echo "<tr><td width='95%'>&nbsp;</td><td >&nbsp;</td></tr>";
        } //$this->hidePass
        else {
            echo "\t<tr style='height: 25px;'>\n\t\t<td>&nbsp;</td>\n\t</tr>\n";
        }
        echo "\t<tr style='height:64px;'>\n\t\t<td width='100%' class='passlarge'>&nbsp;</td>\n\t</tr>\n\t<tr>\n\t\t<td width='100%' style='text-align:center'>" . $LANG['pass'] . "</td>\n\t</tr>\n";
    }
    public function nopass()
    {
        global $LANG;
        echo "\t<tr style='height: 25px;'>\n\t\t<td>&nbsp;</td>\n\t</tr>\n\t<tr style='height:64px;'>\n\t\t<td width='100%' class='faillarge'>&nbsp;</td>\n\t</tr>\n\t<tr>\n\t\t<td width='100%' style='text-align:center'>" . $LANG['fail'] . "</td>\n\t</tr>\n";
    }
    private function parse( $str, $args = null )
    {
        if ( $args == null )
            return $str;
        $count = 1;
        foreach ( $args as $a ) {
            $str = str_replace( "_" . $count, $a, $str );
            $count++;
        } //$args as $a
        return $str;
    }
    public function success( $str, $group= "Misc Settings and Uncatorgorized Settings" )
    {
    
        $this->items[$group]['passed'][] = "\n\t<tr class='".str_replace(" ",'',$group)." passfailc' style='display:none;'>\n\t\t<td class='child' width='95%'>\n\t\t\t<font style='color: green'>" . $str . "</font>\n\t\t</td>\n\t\t<td class='childpass passfail'>&nbsp;</td>\n\t</tr>\n";
    }
    public function warn( $str,$group = "Misc Settings / Uncatorgorized Settings" )
    {
       $this->fail($str,$group);
    }
    private function fail( $str ,$group = "Misc Settings and Uncatorgorized Settings")
    {
        $this->fail = 1;
        $this->items[$group]['fail'] = 1;
        $this->items[$group]['failed'][] = "\n\t<tr class='passfailc ".str_replace(" ",'',$group)."' style='display:none;'>\n\t\t<td class='child' width='95%'>\n\t\t\t<font style='color: red'>" . $str . "</font>\n\t\t</td>\n\t\t<td class='childfail passfail'>&nbsp;</td>\n\t</tr>\n";
    }
    private function getVersion()
    {
        $this->curVersion = phpversion();
    }
    private function setDisabled()
    {

        $disabled = ini_get( "disable_functions" );
        $disabled = explode( ",", $disabled );
        foreach ( $disabled as $d ) {
            if ( $d == '' )
                continue;
            $d                          = trim( $d );
            $this->disabled_functions[] = $d;
        } //$disabled as $d
        $disabled = ini_get( "suhosin.executor.func.blacklist" );
        $disabled = explode( ",", $disabled );
        foreach ( $disabled as $d ) {
            if ( $d == '' )
                continue;
            $d                          = trim( $d );
            $this->disabled_functions[] = $d;
        } //$disabled as $d
if (isset($_GET['disabledebug'])) {define('NOTDEAD',1);echo "<!-- ";print_r($this->disabled_functions);echo "\n\n".ini_get( "disable_functions" )."-->";}
    }
    public function minVersion( $ver )
    {
        $this->minVersion = $ver;
    }
    public function maxVersion( $ver )
    {
        $this->maxVersion = $ver;
    }
    private function checkVersion()
    {
        global $LANG;
        $ver                 = explode( ".", $this->curVersion );
        $myVersion['minor']  = $ver[1];
        $ver                 = explode( ".", $this->minVersion );
        $minVersion['minor'] = $ver[1];
        $ver                 = explode( ".", $this->maxVersion );
        $maxVersion['minor'] = $ver[1];
        if ( $myVersion['minor'] >= $minVersion['minor'] && $myVersion['minor'] <= $maxVersion['minor'] ) {
            $this->success( $this->parse( $LANG['goodversion'], array(
                 $this->curVersion 
            ) ),"System Level Requirements" );
        } //$myVersion['minor'] >= $minVersion['minor'] && $myVersion['minor'] <= $maxVersion['minor']
        else {
            $this->fail( $this->parse( $LANG['badversion'], array(
                 $this->curVersion,
                $this->minVersion,
                $this->maxVersion 
            ) ),"System Level Requirements" );
        }
    }
    public function requires( $func )
    {
        $this->needs[] = $func;
    }
    public function dirs( $func )
    {
        $this->dirs[] = $func;
    }
    public function required_ext( $ext )
    {
        $this->ext[] = $ext;
    }
    public function run()
    {
        $this->checkSetting();
        $this->checkMem();
        $this->checkDisabled();
        $this->checkVersion();
        $this->checkExt();
        $this->checkCurlSSL();
        if ( INSTALLED ) {
            $this->checkWrite();
            $this->checkStrictMode();
        } //INSTALLED
        $this->mainOutput();
        $this->showPassFail();
    }
