          });
          $(".collapse a").click(function(){
          $('.passfailc').hide();
          $('.passfailp').removeClass('parent2');
          $('.passfailp').addClass('parent1');
          });
        <?php
        foreach($this->items as $name=>$ignore) {
      	    $pass = ($this->items[$name]['fail'] == 1)?"fail":"pass";
            echo '$(".'.str_replace(" ",'',$name).$pass.'").click(function() {
              if ($(this).hasClass("parent1")) {
                $(this).removeClass("parent1");
                $(this).addClass("parent2");
              } else {
                $(this).removeClass("parent2");
                $(this).addClass("parent1");
              }
              $(".'.str_replace(" ",'',$name).'").toggle();
              
              });';
    	}
    	echo "</script>";
	
    }
    }
    public function showPassFail($check=1)
    {
        if ( $this->fail || $check === 0 ) {
            $this->nopass();
        } //$this->fail
        else {
            if ($this->warn == 1) 
                $this->warning();
            else
                $this->pass();
        }
    }
    public function warning() 
    {
        global $LANG;
        echo "\t<tr style='height: 25px;'>\n\t\t<td>&nbsp;</td>\n\t</tr>\n\t<tr style='height:64px;'>\n\t\t<td width='100%' class='warnlarge'>&nbsp;</td>\n\t</tr>\n\t<tr>\n\t\t<td width='100%' style='text-align:center'>" . $LANG['warn'] . "</td>\n\t</tr>\n";
    }
    private function checkCurlSSL()
    {
        global $LANG;
        if ( extension_loaded( "curl" ) ) {
            $version       = curl_version();
            $ssl_supported = ( $version['features'] & CURL_VERSION_SSL );
            if ( $ssl_supported ) {
                $this->success( $this->parse( $LANG['curlssl'] ) ,"List of required PHP extensions");
            } //$ssl_supported
            else {
                $this->fail( $this->parse( $LANG['curlnossl'] ),"List of required PHP extensions" );
            }
        } //extension_loaded( "curl" )
        else {
            $this->fail( $this->parse( $LANG['nocurl'] ),"List of required PHP extensions" );
        }
    }
    private function checkExt()
    {
        global $LANG;
        foreach ( $this->ext as $ext ) {
            if ( extension_loaded( $ext ) ) {
                $this->success( $this->parse( $LANG['extloaded'], array(
                     $ext 
                ) ) ,"List of required PHP extensions");
            } //extension_loaded( $ext )
            else {
                $this->fail( $this->parse( $LANG['extnotloaded'], array(
                     $ext 
                ) ) ,"List of required PHP extensions");
            }
        } //$this->ext as $ext
                $this->checkExt2();
    }
    public function warn_ext( $ext )
    {
        $this->extw[] = $ext;
    }
    private function checkExt2()
    {
        global $LANG;
        foreach ( $this->extw as $ext ) {
            if ( extension_loaded( $ext ) ) {
                $this->success( $this->parse( $LANG['extloaded'], array(
                     $ext 
                ) ) );
            } //extension_loaded( $ext )
            else {
                $this->warn( $this->parse( $LANG['extnotloaded'], array(
                     $ext 
            ) ) );
            }
        } //$this->ext as $ext
    }
    private function checkStrictMode()
    {
        global $LANG;
        ob_start();
        @include_once 'init.php';
        @include_once '../init.php';
        define( 'NOTDEAD', 1 );
        $data = full_query( "select @@sql_mode" );
        $data = mysql_fetch_array( $data );
        $data = $data[0];
        if ( $data != "" ) {
            $this->fail( $this->parse( $LANG['strictmodeon'], array(
                 $data 
            ) ) ,"System Level Requirements");
        } //$data != ""
        else {
            $this->success( $this->parse( $LANG['strictmodeoff'], array ()),"System Level Requirements" );
        }
    }
    private function checkMem()
    {
        global $LANG;
        $mem = preg_replace( "/([0-9]*)(.*)/", "$1", ini_get( "memory_limit" ) );
        if ( $mem >= $this->reqMem ) {
            $this->success( $this->parse( $LANG['goodmem'], array(
                 $mem,
                $this->reqMem 
            ) ) ,"System Level Requirements");
        } //$mem >= $this->reqMem
        else {
            $this->fail( $this->parse( $LANG['badmem'], array(
                 $mem,
                $this->reqMem 
            ) ),"System Level Requirements" );
        }
    }
    public function setMem( $mem )
    {
        $this->reqMem = $mem;
    }
    private function checkDisabled()
    {
        global $LANG;
        foreach ( $this->needs as $func ) {
            if ( in_array( $func, $this->disabled_functions ) ) {
                $this->fail( $this->parse( $LANG['disabledfunc'], array(
                     $func 
                ) ),"Ensure the required functions for WHMCS are not disabled" );
            } //in_array( $func, $this->disabled_functions )
            else {
                $this->success( $this->parse( $LANG['nondisabledfunc'], array(
                     $func 
                ) ) ,"Ensure the required functions for WHMCS are not disabled");
            }
        } //$this->needs as $func
    }
    private function checkWrite()
    {
        global $LANG;
        foreach ( $this->dirs as $dir ) {
            if (defined('INADMIN')) $dir = '../'.$dir;
            if ( !file_exists( $dir ) ) {
                $this->fail( $this->parse( $LANG['dirnoexists'], array(
                     __DIR__.'/'.$dir 
                ) ) ,"Check File and Directory Permissions");
            } //!file_exists( $dir )
            elseif ( !is_writeable( $dir ) || !is_readable( $dir ) ) {
                $this->fail( $this->parse( $LANG['dirnowrite'], array(
                     __DIR__.'/'.$dir 
                ) ) ,"Check File and Directory Permissions");
            } //!is_writeable( $dir ) || !is_readable( $dir )
            else {
                $this->success( $this->parse( $LANG['dirwrite'], array(
                     __DIR__.'/'.$dir 
                ) ) ,"Check File and Directory Permissions");
            }
        } //$this->dirs as $dir
    }
}

